mapper.select:
SELECT DECODE(COUNT(*),1,1,0)
FROM t_org_info
WHERE ORGCODE = #{loginUserOrgCode}

SELECT user_id , user_name, userrole, juminno, orgcode , org_name , test_yn FROM ( SELECT b.user_id, a.user_name, a.userrole, a.juminno, 'ORG0001' as orgcode , (select org_name from T_ORG_INFO where ORGCODE = 'ORG0001') as org_name , 'corona_test_yn:'||a.CORONA_TEST_YN as test_yn FROM t_org0001 a, t_user b, t_user_mapping c WHERE a.juminno = b.juminno AND a.juminno = c.juminno AND c.orgcode = 'ORG0001' UNION ALL SELECT b.user_id, a.user_name, a.userrole, a.juminno, 'ORG0002' as orgcode , (select org_name from T_ORG_INFO where ORGCODE = 'ORG0002') as org_name , 'eye_test_yn:'||a.EYE_TEST_YN as test_yn FROM t_org0002 a, t_user b, t_user_mapping c WHERE a.juminno = b.juminno AND a.juminno = c.juminno AND c.orgcode = 'ORG0002' ) WHERE 1=1
AND user_id = #{loginUserId}
AND orgcode = #{loginUserOrgCode}
